import React, { useState } from 'react';
import './App.css'; // 导入 CSS 文件
import AppointmentForm from './component/AppointmentForm';
import AppointmentList from './component/AppointmentList';
import AppointmentDetails from './component/AppointmentDetails';

function App() {
    const [selectedAppointmentId, setSelectedAppointmentId] = useState(null);

    const handleAppointmentAdded = () => {
        setSelectedAppointmentId(null);
    };

    const handleSelectAppointment = (apointment_id) => {
        setSelectedAppointmentId(apointment_id);
    };

    return (
        <div>
            <h1>Medical Appointment Booking System</h1>
            <AppointmentForm onAppointmentAdded={handleAppointmentAdded} />
            <AppointmentList onSelectAppointment={handleSelectAppointment} />
            <AppointmentDetails appointmentId={selectedAppointmentId} />
        </div>
    );
}

export default App;
