import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AppointmentDetails({ appointmentId }) {
    const [appointment, setAppointment] = useState(null);

    useEffect(() => {
        const fetchAppointment = async () => {
            try {
                const response = await axios.get(`http://localhost:8091/appointments/${appointmentId}`);
                setAppointment(response.data);
            } catch (error) {
                console.error('Error fetching appointment details:', error);
            }
        };

        if (appointmentId) {
            fetchAppointment();
        }
    }, [appointmentId]);

    const handleCancel = async () => {
        try {
            await axios.delete(`http://localhost:8091/appointments/${appointmentId}`);
            setAppointment(null);
        } catch (error) {
            console.error('Error canceling appointment:', error);
        }
    };

    if (!appointment) {
        return <div>Select an appointment to see details</div>;
    }

    return (
        <div>
            <h2>Appointment Details</h2>
            <p>Patient: {appointment.patientName}</p>
            <p>Doctor: {appointment.doctorName}</p>
            <p>Date: {appointment.date}</p>
            <p>Time: {appointment.time}</p>
            <button onClick={handleCancel}>Cancel Appointment</button>
        </div>
    );
}

export default AppointmentDetails;
